import { useEffect, useState } from "react"

export default function App() {
  const [users, setUsers] = useState([])
  const [nome, setNome] = useState("")
  const [email, setEmail] = useState("")
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [submitting, setSubmitting] = useState(false)

  useEffect(() => {
    fetchUsers()
  }, [])

  const fetchUsers = async () => {
    try {
      const response = await fetch("http://localhost/backend/api/listar.php")
      if (!response.ok) throw new Error("Erro ao carregar usuários")
      
      const data = await response.json()
      setUsers(data)
    } catch (err) {
      setError(err.message || "Erro desconhecido")
    } finally {
      setLoading(false)
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    setSubmitting(true)
    setError(null)

    try {
      const response = await fetch("http://localhost/backend/api/cadastrar.php", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ nome, email }),
      })

      const data = await response.json()

      if (!response.ok) throw new Error(data.error || "Erro ao cadastrar usuário")
      
      if (data.data) {
        setUsers([...users, data.data])
        setNome("")
        setEmail("")
      }
    } catch (err) {
      setError(err.message || "Erro ao cadastrar usuário")
    } finally {
      setSubmitting(false)
    }
  }

  if (loading) {
    return (
      <div className="loading">Carregando...</div>
    )
  }

  return (
    <div className="container">
      <h1>Lista de Usuários</h1>
      
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="nome">Nome:</label>
          <input
            id="nome"
            type="text"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            required
            disabled={submitting}
          />
        </div>
        
        <div>
          <label htmlFor="email">Email:</label>
          <input
            id="email"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            disabled={submitting}
          />
        </div>

        <button type="submit" disabled={submitting}>
          {submitting ? "Cadastrando..." : "Adicionar Usuário"}
        </button>
      </form>

      {error && (
        <div className="error">
          {error}
        </div>
      )}

      {users.length > 0 ? (
        <ul className="users-list">
          {users.map((user) => (
            <li key={user.id}>
              <strong>{user.nome}</strong> - {user.email}
            </li>
          ))}
        </ul>
      ) : (
        <p>Nenhum usuário cadastrado</p>
      )}
    </div>
  )
}
