import { useEffect, useState } from "react"
import "./index.css"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faInstagram, faFacebook, faTwitter } from '@fortawesome/free-brands-svg-icons';
import { FaInstagram, FaFacebook, FaTwitter } from 'react-icons/fa';


export default function App() {
  const [users, setUsers] = useState([])
  const [formData, setFormData] = useState({
    nome: "",
    dataNascimento: "",
    cpf: "",
    email: "",
    telefone: "",
    endereco: "",
    cidade: "",
    estado: "",
  })
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [submitting, setSubmitting] = useState(false)

  useEffect(() => {
    fetchUsers()
  }, [])

  const fetchUsers = async () => {
    try {
      const response = await fetch("http://localhost/sistema-cadastro-cliente/backend/listar")
      if (!response.ok) throw new Error("Erro ao carregar usuários")

      const data = await response.json()
      setUsers(data.data)
    } catch (err) {
      setError(err.message || "Erro desconhecido")
    } finally {
      setLoading(false)
    }
  }

  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }))
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    setSubmitting(true)
    setError(null)

    try {
      const response = await fetch("http://localhost/sistema-cadastro-cliente/backend/cadastrar", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      })

      const data = await response.json()

      if (!response.ok) throw new Error(data.error || "Erro ao cadastrar usuário")

      if (data.data) {
        setUsers([data.data, ...users])
        setFormData({
          nome: "",
          dataNascimento: "",
          cpf: "",
          email: "",
          telefone: "",
          endereco: "",
          cidade: "",
          estado: "",
        })
      }
    } catch (err) {
      setError(err.message || "Erro ao cadastrar usuário")
    } finally {
      setSubmitting(false)
    }
  }

  if (loading) {
    return <div className="loading">Carregando...</div>
  }

  return (
    <div className="app-container">
      <div className="blur-effect top-left"></div>
      <div className="blur-effect bottom-right"></div>

      <div className="content-container">
        <div className="left-column">
          <div className="logo-section">
            <h1>ASA</h1>
            <h2>Personalizados</h2>
            <p>Transformando Ideias em Realidade</p>
          </div>

          <div className="social-icons">
            <a href="#" className="social-icon">
            <FaInstagram />
            </a>
      <a href="#" className="social-icon">
        <FaFacebook />
      </a>
      <a href="#" className="social-icon">
        <FaTwitter />
      </a>
    </div>

          {users.length > 0 && (
            <div className="users-list">
              <h3>Usuários Cadastrados</h3>
              <ul>
                {users.map((user) => (
                  <li key={user.id}>
                    <strong>{user.nome}</strong>
                    <br />
                    <span>{user.email}</span>
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>

        <div className="right-column">
          <h3>Formulário de Cadastro</h3>
          <h4>Dados Pessoais:</h4>

          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Nome Completo:</label>
              <input type="text" name="nome" value={formData.nome} onChange={handleChange} required />
            </div>

            <div className="form-group">
              <label>Data de Nascimento:</label>
              <input
                type="date"
                name="dataNascimento"
                value={formData.dataNascimento}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label>CPF:</label>
              <input
                type="text"
                name="cpf"
                value={formData.cpf}
                onChange={handleChange}
                required
                placeholder="___.___.___-__"
              />
            </div>

            <h4>Contato:</h4>

            <div className="form-group">
              <label>E-mail:</label>
              <input type="email" name="email" value={formData.email} onChange={handleChange} required />
            </div>

            <div className="form-group">
              <label>Telefone:</label>
              <input
                type="tel"
                name="telefone"
                value={formData.telefone}
                onChange={handleChange}
                required
                placeholder="(__) ____-____"
              />
            </div>

            <div className="form-group">
              <label>Endereço:</label>
              <input type="text" name="endereco" value={formData.endereco} onChange={handleChange} required />
            </div>

            <div className="form-group">
              <label>Cidade:</label>
              <input type="text" name="cidade" value={formData.cidade} onChange={handleChange} required />
            </div>

            <div className="form-group">
              <label>Estado:</label>
              <input type="text" name="estado" value={formData.estado} onChange={handleChange} required maxLength={2} />
            </div>

            {error && <div className="error-message">{error}</div>}

            <button type="submit" disabled={submitting} className="submit-button">
              {submitting ? "Cadastrando..." : "Cadastrar"}
            </button>
          </form>
        </div>
      </div>
    </div>
  )
}
