import { useEffect, useState } from "react"
import { Facebook, Instagram, Twitter } from "lucide-react"


export default function App() {
  const [users, setUsers] = useState([])
  const [formData, setFormData] = useState({
    nome: "",
    dataNascimento: "",
    cpf: "",
    email: "",
    telefone: "",
    endereco: "",
    cidade: "",
    estado: "",
  })
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)
  const [submitting, setSubmitting] = useState(false)

  useEffect(() => {
    fetchUsers()
  }, [])

  const fetchUsers = async () => {
    try {
      const response = await fetch("http://localhost/sistema-cadastro-cliente/backend/listar")
      if (!response.ok) throw new Error("Erro ao carregar usuários")

      const data = await response.json()
      setUsers(data.data)
    } catch (err) {
      setError(err.message || "Erro desconhecido")
    } finally {
      setLoading(false)
    }
  }

  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }))
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    setSubmitting(true)
    setError(null)

    try {
      const response = await fetch("http://localhost/sistema-cadastro-cliente/backend/cadastrar", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      })

      const data = await response.json()

      if (!response.ok) throw new Error(data.error || "Erro ao cadastrar usuário")

      if (data.data) {
        setUsers([data.data, ...users])
        setFormData({
          nome: "",
          dataNascimento: "",
          cpf: "",
          email: "",
          telefone: "",
          endereco: "",
          cidade: "",
          estado: "",
        })
      }
    } catch (err) {
      setError(err.message || "Erro ao cadastrar usuário")
    } finally {
      setSubmitting(false)
    }
  }

  if (loading) {
    return <div className="loading">Carregando...</div>
  }

  return (
    <div className="min-h-screen bg-emerald-800 p-4 flex items-center justify-center relative overflow-hidden">
      {/* Efeitos de blur decorativos */}
      <div className="absolute top-1/4 -left-20 w-40 h-40 rounded-full bg-black/20 blur-3xl" />
      <div className="absolute bottom-1/4 -right-20 w-40 h-40 rounded-full bg-black/20 blur-3xl" />

      <div className="w-full max-w-4xl bg-emerald-800/90 border-0 shadow-2xl text-white rounded-lg">
        <div className="p-8 grid md:grid-cols-2 gap-8">
          {/* Coluna da esquerda - Logo e redes sociais */}
          <div className="space-y-8">
            <div className="space-y-2">
              <h1 className="text-5xl font-bold">ASA</h1>
              <h2 className="text-4xl font-bold">Personalizados</h2>
              <p className="text-xl">Transformando Ideias em Realidade</p>
            </div>

            <div className="flex gap-4">
              <a href="#" className="hover:opacity-80">
                <Instagram size={24} />
              </a>
              <a href="#" className="hover:opacity-80">
                <Facebook size={24} />
              </a>
              <a href="#" className="hover:opacity-80">
                <Twitter size={24} />
              </a>
            </div>

            {/* Lista de usuários */}
            {users.length > 0 && (
              <div className="mt-8">
                <h3 className="text-xl font-semibold mb-4">Usuários Cadastrados</h3>
                <ul className="space-y-2">
                  {users.map((user) => (
                    <li key={user.id} className="bg-white/10 p-3 rounded">
                      <strong>{user.nome}</strong>
                      <br />
                      <span className="text-sm opacity-75">{user.email}</span>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>

          {/* Coluna da direita - Formulário */}
          <div className="space-y-6">
            <div>
              <h3 className="text-2xl font-semibold mb-4">Formulário de Cadastro</h3>
              <h4 className="text-xl mb-4">Dados Pessoais:</h4>
            </div>

            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label className="block mb-1">Nome Completo:</label>
                <input
                  type="text"
                  name="nome"
                  value={formData.nome}
                  onChange={handleChange}
                  required
                  className="w-full bg-transparent border border-white/20 rounded p-2 text-white placeholder:text-white/50"
                />
              </div>

              <div>
                <label className="block mb-1">Data de Nascimento:</label>
                <input
                  type="date"
                  name="dataNascimento"
                  value={formData.dataNascimento}
                  onChange={handleChange}
                  required
                  className="w-full bg-transparent border border-white/20 rounded p-2 text-white placeholder:text-white/50"
                />
              </div>

              <div>
                <label className="block mb-1">CPF:</label>
                <input
                  type="text"
                  name="cpf"
                  value={formData.cpf}
                  onChange={handleChange}
                  required
                  placeholder="___.___.___-__"
                  className="w-full bg-transparent border border-white/20 rounded p-2 text-white placeholder:text-white/50"
                />
              </div>

              <div>
                <h4 className="text-xl mb-4">Contato:</h4>
                <div className="space-y-4">
                  <div>
                    <label className="block mb-1">E-mail:</label>
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      required
                      className="w-full bg-transparent border border-white/20 rounded p-2 text-white placeholder:text-white/50"
                    />
                  </div>

                  <div>
                    <label className="block mb-1">Telefone:</label>
                    <input
                      type="tel"
                      name="telefone"
                      value={formData.telefone}
                      onChange={handleChange}
                      required
                      placeholder="(__) ____-____"
                      className="w-full bg-transparent border border-white/20 rounded p-2 text-white placeholder:text-white/50"
                    />
                  </div>
                </div>
              </div>

              <div>
                <label className="block mb-1">Endereço:</label>
                <input
                  type="text"
                  name="endereco"
                  value={formData.endereco}
                  onChange={handleChange}
                  required
                  className="w-full bg-transparent border border-white/20 rounded p-2 text-white placeholder:text-white/50"
                />
              </div>

              <div>
                <label className="block mb-1">Cidade:</label>
                <input
                  type="text"
                  name="cidade"
                  value={formData.cidade}
                  onChange={handleChange}
                  required
                  className="w-full bg-transparent border border-white/20 rounded p-2 text-white placeholder:text-white/50"
                />
              </div>

              <div>
                <label className="block mb-1">Estado:</label>
                <input
                  type="text"
                  name="estado"
                  value={formData.estado}
                  onChange={handleChange}
                  required
                  maxLength={2}
                  className="w-full bg-transparent border border-white/20 rounded p-2 text-white placeholder:text-white/50"
                />
              </div>

              {error && <div className="bg-red-500/20 text-white p-3 rounded">{error}</div>}

              <button
                type="submit"
                disabled={submitting}
                className="w-full bg-gradient-to-r from-blue-400 to-blue-600 hover:from-blue-500 hover:to-blue-700 text-white font-semibold py-3 rounded disabled:opacity-50"
              >
                {submitting ? "Cadastrando..." : "Cadastrar"}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  )
}
