import { useEffect, useState } from "react";

function App() {
  const [usuarios, setUsuarios] = useState([]);
  const [nome, setNome] = useState("");
  const [email, setEmail] = useState("");

  // Função para obter os usuários da API
  useEffect(() => {
    fetch("http://localhost/backend/api/listar.php")
      .then((res) => res.json())
      .then((data) => setUsuarios(data))
      .catch((error) => console.error("Erro:", error));
  }, []);

  // Função para enviar o formulário de cadastro
  const handleSubmit = (e) => {
    e.preventDefault();

    const novoUsuario = { nome, email };

    fetch("http://localhost/backend/api/cadastrar.php", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(novoUsuario),
    })
      .then((res) => res.json())
      .then((data) => {
        setUsuarios([...usuarios, data]);  // Atualiza a lista de usuários com o novo usuário
        setNome(""); // Limpa o campo do nome
        setEmail(""); // Limpa o campo do email
      })
      .catch((error) => console.error("Erro:", error));
  };

  return (
    <div>
      <h1>Lista de Usuários</h1>
      
      {/* Formulário para adicionar usuários */}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nome:</label>
          <input
            type="text"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <button type="submit">Adicionar Usuário</button>
      </form>

      {/* Lista de usuários */}
      <ul>
        {usuarios.map((user) => (
          <li key={user.id}>
            {user.nome} - {user.email}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
