import { useState, useEffect } from "react"
import Navbar from "../components/Navbar"
import UserList from "../components/UserList"
import UserModal from "../components/UserModal"

function UserManagement() {
  const [users, setUsers] = useState([])
  const [filteredUsers, setFilteredUsers] = useState([])
  const [selectedUser, setSelectedUser] = useState(null)
  const [isModalOpen, setIsModalOpen] = useState(false)
  const [searchQuery, setSearchQuery] = useState("")

  useEffect(() => {
    // Fetch users from API or database
    const fetchUsers = async () => {
      try {
        const response = await fetch("/api/users") // Replace with your API endpoint
        const data = await response.json()
        setUsers(data)
        setFilteredUsers(data)
      } catch (error) {
        console.error("Error fetching users:", error)
      }
    }
    fetchUsers()
  }, [])

  useEffect(() => {
    const filtered = users.filter((user) => user.name.toLowerCase().includes(searchQuery.toLowerCase()))
    setFilteredUsers(filtered)
  }, [searchQuery, users])

  const handleSearch = (query) => {
    setSearchQuery(query)
  }

  const handleUserSelect = (user) => {
    setSelectedUser(user)
    setIsModalOpen(true)
  }

  const handleCloseModal = () => {
    setSelectedUser(null)
    setIsModalOpen(false)
  }

  const handleSaveUser = (updatedUser) => {
    // Update user in API or database
    const updatedUsers = users.map((user) => (user.id === updatedUser.id ? updatedUser : user))
    setUsers(updatedUsers)
    setFilteredUsers(updatedUsers)
    handleCloseModal()
  }

  const handleDeleteUser = (userId) => {
    // Delete user from API or database
    const updatedUsers = users.filter((user) => user.id !== userId)
    setUsers(updatedUsers)
    setFilteredUsers(updatedUsers)
    handleCloseModal()
  }

  return (
    <div className="user-management">
      <div className="container">
        <Navbar onSearch={handleSearch} />
        <main className="main-content">
          <UserList users={filteredUsers} onUserSelect={handleUserSelect} />
          {isModalOpen && selectedUser && (
            <UserModal
              user={selectedUser}
              onClose={handleCloseModal}
              onSave={handleSaveUser}
              onDelete={handleDeleteUser}
            />
          )}
        </main>
      </div>
    </div>
  )
}

export default UserManagement
